import { Component, OnInit } from "@angular/core";
import { ApiServiceService } from "src/app/services/api-service.service";

@Component({
  selector: "home",
  templateUrl: "./home.html",
  styleUrls: ["./home.scss"],
})
export class DisplayComponent implements OnInit {
  startingDate: any;
  numberOfDates: number;
  endDate: Date;
  minDate: string;

  constructor(private apiService: ApiServiceService) {
    const today = new Date();
    this.minDate = this.formatDate(today);
  }

  formatDate(date: Date): string {
    const year = date.getFullYear();
    const month = String(date.getMonth() + 1).padStart(2, "0");
    const day = String(date.getDate()).padStart(2, "0");
    return `${year}-${month}-${day}`;
  }

  updateMinDate(selectedDate: string): void {
    const currentDate = new Date();
    const selected = new Date(selectedDate);
    if (selected < currentDate) {
      this.startingDate = null; // Reset startingDate if a previous date is selected
    }
    this.minDate = this.formatDate(currentDate);
  }

  ngOnInit() {}

  generateEndDate(): void {
    debugger
    if (!this.startingDate || !this.numberOfDates) {
      alert("Please enter required data.");
      return;
    }

    const startDate = this.reformatDate(this.startingDate);
    this.apiService.getDates(startDate, this.numberOfDates).subscribe(
      (response) => {
        console.log(response.endDate);
        const dateString = response.endDate;
        const parts = dateString.split("/");
        const day = Number(parts[0]);
        const month = Number(parts[1]) - 1; // Subtract 1 because months are zero-based
        const year = Number(parts[2]);

        this.endDate = new Date(year, month, day);
        console.log(this.endDate);
      },
      (error) => {
        console.error(error);
      }
    );
  }

  reformatDate(date: string): string {
    const parts = date.split("-");
    const year = parts[0];
    const month = parts[1];
    const day = parts[2];
    return `${year}/${month}/${day}`;
  }

  getMinDate(): string {
    const today = new Date();
    const year = today.getFullYear();
    const month = String(today.getMonth() + 1).padStart(2, "0");
    const day = String(today.getDate()).padStart(2, "0");
    return `${year}-${month}-${day}`;
  }
}
<div class="container-bg">
  <div class="container-fluid">
    <div class="row">
      <div class="col-4"></div>
      <div class="col-4">
        <div
          class="card"
          style="width: 100%; margin-top: 50%; margin-left: 20%"
        >
          <div class="card-body">
            <h5 class="card-title">Task Time Date Calculator </h5>
            <h6 class="card-subtitle mb-2 text-body-secondary">
            </h6>
            <div class="card-data">
              <label>Enter Starting date</label>
              <input
                class="form-control"
                type="Date"
                placeholder="Starting Dates"
                aria-label="default input example"
                [(ngModel)]="startingDate"
                [min]="minDate"
              />
              <br />
              <label>Enter Number of Dates </label>
              <input
                [(ngModel)]="numberOfDates"
                class="form-control"
                type="number"
                aria-label="default input example"
                min="1"
                step="1"
              />
              <br />
              <div >
                Your end Date
                <span>{{
                  endDate | date : "dd/MM/yyyy"
                }}</span>
              </div>
            </div>

            <button
              type="button"
              class="btn btn-primary"
              (click)="generateEndDate()"
            >
              Generate
            </button>
          </div>
        </div>
      </div>
      <div class="col-4"></div>
    </div>
  </div>
</div>
